INSERT INTO `tbl_product` (`id`, `name`, `description`, `price`, `quantity`, `status`, `created_at`, `updated_at`, `category_id`, `discount_id`, `name_unaccent`) VALUES
      ('18d3f905-00ff-40fb-a833-cbab9c02614e', 'Cup cake 1', 'Cupcake 1 là sự kết hợp hoàn hảo giữa cốt bánh mềm mịn và lớp kem phủ béo ngậy, mang đến trải nghiệm vị giác tuyệt vời ngay từ lần thử đầu tiên. Cốt bánh được làm từ bột mì cao cấp, trứng gà tươi và sữa nguyên chất, nướng vừa tới để giữ được độ ẩm và sự mềm mại. Phía trên là lớp kem bơ vani được đánh bông nhẹ nhàng, trang trí bằng socola vụn và trái cây tươi, tạo nên vẻ ngoài bắt mắt và cuốn hút. Với hương vị ngọt ngào, dịu nhẹ, Cupcake 1 không chỉ phù hợp cho những buổi tiệc trà, sinh nhật, mà còn là món quà tinh tế dành tặng người thân, bạn bè trong những dịp đặc biệt.', 16000, 47, 1, '2025-04-11 08:18:43.526000', '2025-04-25 23:52:47.684000', 'e121da67-bd67-4d7c-ab82-4b84e785651e', '8bcfd297-1556-11f0-8ac6-0242ac110002', 'cup cake 1'),
      ('1b5c9886-e07e-470a-81b4-d5ca587c4a2b', 'Bánh cưới 9', 'Bánh cưới 9 là một kiệt tác nghệ thuật dành riêng cho ngày trọng đại của bạn. Được thiết kế theo phong cách cổ điển xen lẫn hiện đại, bánh gồm nhiều tầng được xếp chồng tinh tế, mỗi tầng là một lớp bánh bông lan mềm mịn, xen kẽ với lớp kem tươi ngọt ngào và nhân trái cây tươi mát như dâu tây, việt quất và kiwi. Lớp phủ ngoài là fondant trắng tinh khôi, được tạo hình hoa hồng và lá xanh mềm mại bằng tay, tạo nên vẻ ngoài lãng mạn và sang trọng. Không chỉ đẹp mắt, bánh cưới 9 còn được chú trọng đến từng chi tiết về hương vị – độ ngọt được điều chỉnh vừa phải, đảm bảo chiều lòng mọi thực khách từ trẻ nhỏ đến người lớn tuổi. Đây không chỉ là một chiếc bánh, mà là biểu tượng cho sự khởi đầu ngọt ngào và viên mãn của lứa đôi trong ngày lễ thành hôn.', 323050, 17, 1, '2025-04-11 08:00:08.636000', '2025-04-25 23:52:47.714000', '7751d611-3b80-4cce-b8f6-1b3130d8c992', '8bcfca3e-1556-11f0-8ac6-0242ac110002', 'banh cuoi 9'),
